import React, {Component} from "react";
import {Button, Card, Form} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faEdit} from "@fortawesome/free-solid-svg-icons/faEdit";
import {faPlusSquare, faUndo} from "@fortawesome/free-solid-svg-icons";
import Col from "react-bootstrap/Col";
import {faSave} from "@fortawesome/free-solid-svg-icons/faSave";
import {faList} from "@fortawesome/free-solid-svg-icons/faList";
import {faPlus} from "@fortawesome/free-solid-svg-icons/faPlus";



export default class Achievement extends Component {
    imageId;

    state ={
        taskList: [{index: Math.random(), achievementPara:""}],
        achievementYearHeading:"",
        achievementImage:"",
    }

    handleChange = (e) => {
        if(["achievementPara"].includes(e.target.name)){
            let taskList = [...this.state.taskList]
            taskList[e.target.dataset.id][e.target.name] = e.target.value;
        } else {
            this.setState({[e.target.name]: e.target.value})
        }
    }

    addNewRow = (e) => {
        this.setState((prevState) => ({
            taskList: [...prevState.taskList, {index: Math.random(), achievementPara: ""}]
        }));
    }

    

    render() {
        return (
            <div className="content">
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>Add Achievements</Card.Header>
                    <Form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                        <Card.Body>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridSerial">
                                    <Form.Label>Achievement Year Heading</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Achievement year..." required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                                  value={year}
                                                  onChange={this.bookChange}
                                                  name="year"/>
                                </Form.Group>
                            </Form.Row>
                            <table className="table">
                                <thead>
                                <tr>
                                    <th>Achievement Points</th>
                                </tr>
                                </thead>
                                <tbody>
                                    <TaskList add={this.addNewRow} delete={this.clickOnDelete.bind(this)} taskList={taskList}/>
                                </tbody>
                            </table>


                        </Card.Body>
                        <Card.Footer>
                            <Button size="sm" variant="info" type="button">
                                <FontAwesomeIcon icon={faPlus}/>Add
                            </Button>{' '}
                            <Button size="sm" variant="success" type="submit" onClick={this.addNewRow}>Submit
                            </Button>

                        </Card.Footer>
                    </Form>
                </Card>
            </div>

        );
    }

}
