import React, {Component} from "react";
import {Button, Card, Form} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faEdit} from "@fortawesome/free-solid-svg-icons/faEdit";
import {faPlusSquare, faUndo} from "@fortawesome/free-solid-svg-icons";
import Col from "react-bootstrap/Col";
import {faSave} from "@fortawesome/free-solid-svg-icons/faSave";
import {faList} from "@fortawesome/free-solid-svg-icons/faList";



export default class Achievement extends Component {

    render() {
        return (
            <div className="content">
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>Add Achievements</Card.Header>
                    <Form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                        <Card.Body>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridSerial">
                                    <Form.Label>Achievement Year Heading</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Achievement year..." required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                                  value={year}
                                                  onChange={this.bookChange}
                                                  name="year"/>
                                </Form.Group>
                                <Form.Group as={Col} controlId="formGridFullname">
                                    <Form.Label>Achievement Points</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Full Name.." required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                        // value={this.state.fullname}   you can write like this if doesn't initialize the const {serial, username, password, fullname, email, contact} = this.state;
                                                  value={points}
                                                  onChange={this.bookChange}
                                                  name="points"/>
                                </Form.Group>
                            </Form.Row>


                        </Card.Body>
                        <Card.Footer style={{textAlign: "right"}}>
                            <Button size="sm" variant="success" type="submit">
                                <FontAwesomeIcon icon={faSave}/> {this.state.id ? "Update":"Save"}
                            </Button>{' '}
                            <Button size="sm" variant="info" type="reset">
                                <FontAwesomeIcon icon={faUndo}/> Reset
                            </Button>{' '}
                            <Button size="sm" variant="success" type="button" onClick={this.bookList.bind()}>
                                <FontAwesomeIcon icon={faList}/> Book List
                            </Button>
                        </Card.Footer>
                    </Form>
                </Card>
            </div>

        );
    }

}
