import React, {Component} from "react";
import {Toast} from "react-bootstrap";


export default class MyToast extends Component {
    render() {
        const toastCss = {
            position: 'fixed',
            top: '20px',
            right: '20px',
            boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2), 0 6px 20px 0 rgba(0,0,0,0.19)'
        }

        return (
            <div style={this.props.show ? toastCss : null}>
                {/* eslint-disable-next-line no-template-curly-in-string */}
                <Toast className={'border text-white ${this.props.type === "success" ? "border-primary bg-success" : "border-danger bg-danger"}'} show ={this.props.show}>
                    {/* eslint-disable-next-line no-template-curly-in-string */}
                    <Toast.Header className={'text-white ${this.props.type === "success" ? "bg-success" : "bg-danger"}'} closeButton={false}>
                        <strong className="mr-auto">Success</strong>
                    </Toast.Header>
                    <Toast.Body>
                        {this.props.message}
                    </Toast.Body>
                </Toast>
            </div>

        );
    }

}
