import React, {Component} from "react";
import {Button, Card, Dropdown, Form} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import TaskList from "./TaskList";
import StudentRegisteredService from "../services/StudentRegisteredService";

export default class Achievement extends Component {
    imageId;

    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }


    state = {
        id: '', achievementYearHeading: '', achievementImage: '',
        taskList: [{
            index: Math.random(),
            achievementScores: []
        }]
    };

    initialState ={
        id:'', name:'', description:'', category:''
    }


    handleSubmit = (e) => {
        e.preventDefault();
        const achievementData = {
            achievementYearHeading: this.state.achievementYearHeading,
            achievementImage: {
                id: this.imageId
            },
            achievementScores: this.state.taskList
        };

        StudentRegisteredService.createAchievement(achievementData)
            .then(response => {
                console.log(response)
            }).catch(error => {
            console.log("Error:- " + error)
        })
    };


    handleChange(e) {
        if (["achievementPara"].includes(e.target.name)) {
            let taskList = [...this.state.taskList];
            taskList[e.target.dataset.id][e.target.name] = e.target.value;
            console.log(e.target.value)
        } else {
            this.setState({[e.target.name]: e.target.value})
        }
    }

    handleChangeParty(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }


    addNewRow = () => {
        this.setState((prevState) => ({
            taskList: [...prevState.taskList, {index: Math.random(), achievementPara: ""}]
        }));
    };

    deleteRow = (index) => {
        this.setState({
            taskList: this.state.taskList.filter((s, sindex) => index !== sindex),
        });
    };


    clickOnDelete(record) {
        this.setState({
            taskList: this.state.taskList.filter(r => r !== record)
        })
    }

    onUploadImage(e) {
        let image = e.target.files[0];
        const formData = new FormData();
        formData.append('file', image);
        StudentRegisteredService.uploadFile(formData)
            .then(response => {
                if (response) {
                    this.imageId = response.data.id;
                    console.log(this.imageId)
                }
            })

    }


    render() {
        const {achievementYearHeading, achievementImage, taskList} = this.state;
        const {name, description, category} =this.state


        return (
            <div className="content">
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>Add Achievements</Card.Header>
                    <Form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                        <Card.Body>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridachievementYearHeading">
                                    <Form.Label>Achievement Year Heading</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Achievement year..." required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                                  value={achievementYearHeading}
                                                  onChange={this.handleChange}
                                                  name="achievementYearHeading"/>
                                </Form.Group>
                            </Form.Row>

                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridachievementImage">
                                    <Form.Label>Upload Achievement Image</Form.Label>
                                    <Form.Control type="file" required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                                  value={achievementImage}
                                                  onChange={(e) => this.onUploadImage(e)}
                                        // onChange={this.twoMethod}
                                                  name="achievementImage"/>
                                </Form.Group>
                            </Form.Row>


                            <table className="table">
                                <thead>
                                <tr>
                                    <th>Enter Achievement Points</th>
                                </tr>
                                </thead>
                                <tbody>


                                <TaskList add={this.addNewRow} delete={this.clickOnDelete.bind(this)}
                                          taskList={taskList} onChange={this.handleChange}/>


                                </tbody>
                            </table>


                        </Card.Body>
                        <Card.Footer>
                            <Button size="sm" variant="success" type="submit">Submit
                            </Button>

                        </Card.Footer>
                    </Form>
                </Card>
                <br/>

                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>Add Party</Card.Header>
                    <Form>
                        <Card.Body>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridName">
                                    <Form.Label>Party Name</Form.Label>
                                    <Form.Control type="text" placeholder="Enter name ..." required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                                  value={name}
                                                  onChange={this.handleChangeParty}
                                                  name="name"/>
                                </Form.Group>
                                <Form.Group as={Col} controlId="formGridDescription">
                                    <Form.Label>Party Description</Form.Label>
                                    <Form.Control type="text" placeholder="Enter description year..." required
                                                  className={"bg-dark text-white"}
                                                  autoComplete="off"
                                                  value={description}
                                                  onChange={this.handleChangeParty}
                                                  name="description"/>
                                </Form.Group>
                            </Form.Row>

                            <Dropdown>
                                <Dropdown.Toggle variant="success" id="dropdown-basic">
                                    Select Category
                                </Dropdown.Toggle>

                                <Dropdown.Menu onChange={this.handleChangeParty} name="category" value={category}>
                                    <Dropdown.Item href="#/action-1" value="1">AR1</Dropdown.Item>
                                    <Dropdown.Item href="#/action-2" value="2">AR2</Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>

                        </Card.Body>
                        <Card.Footer>
                            <Button size="sm" variant="success" type="submit">Submit
                            </Button>
                        </Card.Footer>
                    </Form>
                </Card>


            </div>

        );
    }

}
