import React, {Component} from "react";
import {Button, Card, Form} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import {faList} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSave} from "@fortawesome/free-solid-svg-icons/faSave";


export default class Book extends Component {
    constructor(props) {
        super(props);
        this.state = {serial:'', fullname:'', username:'', password:'', email:'', contact:''};
        this.bookChange = this.bookChange.bind(this);
        this.submitBook = this.submitBook.bind(this);
    }

    submitBook(event) {
        alert('Serial: ' +this.state.serial+', Username: ' +this.state.username+ ', Password: ' +this.state.password+', Full Name: ' +this.state.fullname+ ', Email: ' +this.state.email+', Contact: ' +this.state.contact);
        event.preventDefault();
    }

    bookChange(event){
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    render() {
        return (
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header><FontAwesomeIcon icon={faSave} /> Add Book</Card.Header>
                    <Form id="bookFormId" onSubmit={this.submitBook}>
                        <Card.Body>
                        <Form.Row>
                        <Form.Group as = {Col} controlId="formGridSerial">
                            <Form.Label>Serial</Form.Label>
                            <Form.Control type="text" placeholder="Enter Serial Number.." required
                                          className={"bg-dark text-white"}
                                          value={this.state.serial}
                                          onChange={this.bookChange}
                            name="serial" />
                        </Form.Group >
                            <Form.Group as = {Col} controlId="formGridFullname">
                                <Form.Label>Full Name</Form.Label>
                                <Form.Control type="text" placeholder="Enter Full Name.." required
                                              className={"bg-dark text-white"}
                                              value={this.state.fullname}
                                              onChange={this.bookChange}
                                              name="fullname"/>
                            </Form.Group>
                        </Form.Row>

                            <Form.Row>
                                <Form.Group as = {Col} controlId="formGridUsername">
                                    <Form.Label>USERNAME</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Username.." required
                                                  className={"bg-dark text-white"}
                                                  value={this.state.username}
                                                  onChange={this.bookChange}
                                                  name="username"/>
                                </Form.Group>
                                <Form.Group as = {Col} controlId="formGridPassword">
                                    <Form.Label>PASSWORD</Form.Label>
                                    <Form.Control type="password" placeholder="Enter Password.." required
                                                  className={"bg-dark text-white"}
                                                  value={this.state.password}
                                                  onChange={this.bookChange}
                                                  name="password"/>
                                </Form.Group>
                            </Form.Row>

                            <Form.Row>
                                <Form.Group as = {Col} controlId="formGridEmail">
                                    <Form.Label>EMAIL</Form.Label>
                                    <Form.Control type="email" placeholder="Enter Email.." required
                                                  className={"bg-dark text-white"}
                                                  value={this.state.email}
                                                  onChange={this.bookChange}
                                                  name="email"/>
                                </Form.Group>
                                <Form.Group as = {Col} controlId="formGridContact">
                                    <Form.Label>CONTACT</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Contact.." required
                                                  className={"bg-dark text-white"}
                                                  value={this.state.contact}
                                                  onChange={this.bookChange}
                                                  name="contact"/>
                                </Form.Group>
                            </Form.Row>

                </Card.Body>
                <Card.Footer style={{textAlign:"right"}}>
                    <Button size="sm" variant="success" type="submit">
                        <FontAwesomeIcon icon={faSave} /> Submit
                    </Button>
                </Card.Footer>
            </Form>
            </Card>
        );
    }


}
