import React, {Component} from "react";
import {Button, Card, Form} from "react-bootstrap";
import Col from "react-bootstrap/Col";


export default class Book extends Component {
    constructor(props) {
        super(props);
        this.state = {serial:'', fullname:'', username:'', password:'', email:'', contact:''};
    }

    submitBook(event) {
        alert(this.state.serial);
        event.preventDefault();
    }

    bookChange(event){
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    render() {
        return (
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header>Add Book</Card.Header>
                    <Form id="bookFormId" onSubmit={this.submitBook}>
                        <Card.Body>
                        <Form.Row>
                        <Form.Group as = {Col} controlId="">
                            <Form.Label>Serial</Form.Label>
                            <Form.Control type="text" placeholder="Enter Serial Number.."
                                          className={"bg-dark text-white"}
                                          value={this.state.serial}
                                          onChange={this.bookChange} required
                            name="serial" />
                        </Form.Group >
                            <Form.Group as = {Col}>
                                <Form.Label>Full Name</Form.Label>
                                <Form.Control type="text" placeholder="Enter Full Name.."
                                              className={"bg-dark text-white"}
                                              name="fullname"/>
                            </Form.Group>
                        </Form.Row>

                            <Form.Row>
                                <Form.Group as = {Col}>
                                    <Form.Label>USERNAME</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Username.."
                                                  className={"bg-dark text-white"}
                                                  name="username"/>
                                </Form.Group>
                                <Form.Group as = {Col}>
                                    <Form.Label>PASSWORD</Form.Label>
                                    <Form.Control type="password" placeholder="Enter Password.."
                                                  className={"bg-dark text-white"}
                                                  name="password"/>
                                </Form.Group>
                            </Form.Row>

                            <Form.Row>
                                <Form.Group as = {Col}>
                                    <Form.Label>EMAIL</Form.Label>
                                    <Form.Control type="email" placeholder="Enter Email.."
                                                  className={"bg-dark text-white"}
                                                  name="email"/>
                                </Form.Group>
                                <Form.Group as = {Col}>
                                    <Form.Label>CONTACT</Form.Label>
                                    <Form.Control type="text" placeholder="Enter Contact.."
                                                  className={"bg-dark text-white"}
                                                  name="contact"/>
                                </Form.Group>
                            </Form.Row>

                </Card.Body>
                <Card.Footer style={{textAlign:"right"}}>
                    <Button size="sm" variant="success" type="submit">
                        Submit
                    </Button>
                </Card.Footer>
            </Form>
            </Card>
        );
    }


}
