import React, {Component} from "react";
import {Button, Card, Dropdown, Form} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import StudentRegisteredService from "../services/StudentRegisteredService";



class Party extends Component{

    constructor(props) {
        super(props);
        this.handleChangeParty = this.handleChangeParty.bind(this)
        this.handleChangeParty = this.handleChangeParty.bind(this)
    }

    state = {
        id:'', serial:'', description:'', category:''
    }

    handleChangeParty(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmitParty = (e) => {
        e.preventDefault();
        const partyData = {
            name: this.state.name,
            category: {
                id: this.imageId
            },
            description: this.state.description
        };

        StudentRegisteredService.createParty(partyData)
            .then(response => {
                console.log(response)
            }).catch(error => {
            console.log("Error:- " + error)
        })
    };


    render() {

        const {name, description, category} =this.state

        return(
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header>Add Party</Card.Header>
                <Form onSubmit={this.handleSubmitParty}>
                    <Card.Body>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridName">
                                <Form.Label>Party Name</Form.Label>
                                <Form.Control type="text" placeholder="Enter name ..." required
                                              className={"bg-dark text-white"}
                                              autoComplete="off"
                                              value={name}
                                              onChange={this.handleChangeParty}
                                              name="name"/>
                            </Form.Group>
                            <Form.Group as={Col} controlId="formGridDescription">
                                <Form.Label>Party Description</Form.Label>
                                <Form.Control type="text" placeholder="Enter description year..." required
                                              className={"bg-dark text-white"}
                                              autoComplete="off"
                                              value={description}
                                              onChange={this.handleChangeParty}
                                              name="description"/>
                            </Form.Group>
                        </Form.Row>

                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Select Category
                            </Dropdown.Toggle>

                            <Dropdown.Menu onChange={this.handleChangeParty} name="category" value={category}>
                                <Dropdown.Item value="1">AR1</Dropdown.Item>
                                <Dropdown.Item value="2">AR2</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>

                    </Card.Body>
                    <Card.Footer>
                        <Button size="sm" variant="success" type="submit">Submit
                        </Button>
                    </Card.Footer>
                </Form>
            </Card>

        );
    }

}
export default Party;
