import React, {Component} from "react";
import {Card, Table} from "react-bootstrap";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faList} from "@fortawesome/free-solid-svg-icons";
import BookService from "../services/BookService";


export default class BookList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            studentRegistered: []
        }
    }

    componentDidMount() {
        BookService.getAllBooks()
            .then(response => response.data)
            .then(data =>{
               this.setState({studentRegistered : data});
            });
    }

    render() {
        return (
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header><FontAwesomeIcon icon={faList} /> Book List</Card.Header>
                <Card.Body>
                    <Table striped bordered hover variant="dark">
                        <thead>
                        <tr>
                            <th>Serial</th>
                            <th>USERNAME</th>
                            <th>PASSWORD</th>
                            <th>FULLNAME</th>
                            <th>EMAIL</th>
                            <th>CONTACT</th>
                        </tr>
                        </thead>
                        <tbody>
                        {
                            this.state.studentRegistered.length === 0 ?
                            <tr align="center">
                                <td colSpan="6">Books Available</td>
                            </tr>:
                            this.state.studentRegistered.map((book) => (
                            <tr key={studentRegistered.id}>
                            <td>{book.serial}</td>
                            </tr>
                            ))
                        }

                        </tbody>

                    </Table>
                </Card.Body>
            </Card>

        );
    }

}
